import { Component, OnInit } from '@angular/core';


@Component({
  selector: 'app-recipe-page2',
  templateUrl: './recipe-page2.page.html',
  styleUrls: ['./recipe-page2.page.scss'],
})
export class MyComponent {
  cards: { title: string; content: string }[] = [];

  createCard() {
    // Generate new card data (e.g., from user input or an API)
    const newCard = {
      title: 'New Card',
      content: 'This is a dynamically created card.'
    };

    // Push the new card data to the cards array
    this.cards.push(newCard);
  }
}
